export default {
	ID : "ID",
	LIMIT : "LIMIT",
	OFFSET : "OFFSET",
	TRACK_DATA: "TRACK_DATA",
	LOADER_VISIBLITY: "LOADER_VISIBLITY",

	DEFAULT_LIMIT : 3,
	DEFAULT_OFFSET : 0,
	DEFAULT_LOADER_VISIBLITY: true,


	getCurrentLimit : () => {
		if (appsmith.store[storeVariables.LIMIT] === undefined) {
			return storeVariables.DEFAULT_LIMIT
		} else {
			return appsmith.store[storeVariables.LIMIT]
		}
	},

	getCurrentOffset : () => {
		if (appsmith.store[storeVariables.OFFSET] === undefined) {
			return storeVariables.DEFAULT_OFFSET
		} else {
			return appsmith.store[storeVariables.OFFSET]
		}	
	},


	getLoaderVisiblity : () => {
		if (appsmith.store[storeVariables.LOADER_VISIBLITY] === undefined) {
			return storeVariables.DEFAULT_LOADER_VISIBLITY
		} else {
			return appsmith.store[storeVariables.LOADER_VISIBLITY]
		}	

	},

	getRandomChar : () => {
		const characters ='ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
		return characters.charAt(Math.floor(Math.random() * characters.length))
	}
}
