async () => {
  if (appsmith.store[storeVariables.TRACK_DATA] === undefined) {
    await storeValue(storeVariables.OFFSET, storeVariables.DEFAULT_OFFSET);
    await trackSwitcher.trackStoreInit();
  } else {
    await storeValue(storeVariables.ID, appsmith.store[storeVariables.ID] + 1);
  }
  if (appsmith.store[storeVariables.ID] >= appsmith.store[storeVariables.LIMIT]) {
    await storeValue(storeVariables.LOADER_VISIBLITY, true);
    await storeValue(storeVariables.OFFSET, appsmith.store[storeVariables.OFFSET] + appsmith.store[storeVariables.LIMIT]);
    await trackSwitcher.trackStoreInit();
  }
  return appsmith.store;
}

