export default {

	fetchTermFromMongo: async () => {
		var movieTitleDocument = await getRandomMovieTitleDocument.run()

		if (movieTitleDocument !== undefined){
			var term = termFetcher.getTermTransformedFromTitle(movieTitleDocument[0]['title'])
			if (functionUtils.isStringNotEmpty(term)){
				await storeValue(storeVariables.CURRENT_TERM, term )
				return appsmith.store[storeVariables.CURRENT_TERM]
			}
		}
		return
	},

	getTermTransformedFromTitle : ( title) => {
		if (functionUtils.isStringNotEmpty(title)){
			return title.trim().split(' ')[0]
		} else {
			return
		}
	}
}
